sprites.onOverlap(SpriteKind.Player, SpriteKind.Player, function (sprite, otherSprite) {
    sprite.vy = 0
    otherSprite.vy = 0
    if (gameBegun) {
        if (upOrDown1 && !(upOrDown2)) {
            guy1.y += -1
            guy2.y += 1
        } else if (upOrDown2 && !(upOrDown1)) {
            guy1.y += 1
            guy2.y += -1
        }
    }
    onGround1 = true
    onGround2 = true
})
let p1Win = false
let p2Win = false
let upOrDown2 = false
let onGround2 = false
let guy1: Sprite = null
let guy1yv = 0
let upOrDown1 = false
let onGround1 = false
let _2startscreenFinished = false
let guy2yv = 0
let guy2: Sprite = null
let gameBegun = false
let startscreenFinished = false
let check2Player = false
let button2: Sprite = null
let button1: Sprite = null
 scene.setBackgroundImage(projectImages.ggtitlescreen);
button1 = sprites.create(assets.image`PLAYERBUTTON1pressed`, SpriteKind.Player)
button1.setPosition(60, 100)
button2 = sprites.create(assets.image`PLAYER2BUTTON`, SpriteKind.Player)
button2.setPosition(100, 100)
check2Player = false
startscreenFinished = false
gameBegun = false
let cameraX = 80
game.onUpdate(function () {
    if (gameBegun && check2Player) {
        if (guy2.x > guy1.x) {
            scene.centerCameraAt(cameraX, 72)
            cameraX = guy2.x
        } else if (guy2.x < guy1.x) {
            scene.centerCameraAt(cameraX, 72)
            cameraX = guy1.x
        } else {
            scene.centerCameraAt(cameraX, 72)
            cameraX += 1
        }
        if (scene.cameraProperty(CameraProperty.X) - guy1.x > 100) {
            guy1.destroy(effects.disintegrate, 200)
            p2Win = true
        } else if (scene.cameraProperty(CameraProperty.X) - guy2.x > 100) {
            guy2.destroy(effects.disintegrate, 200)
            p1Win = true
        }
    }
    if (!(check2Player) && _2startscreenFinished) {
        scene.centerCameraAt(guy1.x, 72)
    }
})
game.onUpdate(function () {
    if (gameBegun) {
        if (upOrDown1) {
            guy1.vy = 50
        } else {
            guy1.vy = -50
        }
        if (check2Player) {
            if (upOrDown2) {
                guy2.vy = 50
            } else {
                guy2.vy = -50
            }
        }
    }
})
game.onUpdate(function () {
    if (gameBegun) {
        guy1.vx = 50
        if (check2Player) {
            guy2.vx = 50
        }
    }
})
game.onUpdate(function () {
    if (gameBegun) {
        if (guy1.y < 9 || guy1.y > 135) {
            guy1.destroy(effects.disintegrate, 200)
            p2Win = true
        }
        if (check2Player) {
            if (guy2.y < 9 || guy2.y > 135) {
                guy2.destroy(effects.disintegrate, 200)
                p2Win = true
            }
        }
    }
})
game.onUpdate(function () {
    if (scene.tileHitFrom(guy1, CollisionDirection.Bottom) == 15 || scene.tileHitFrom(guy1, CollisionDirection.Top) == 15) {
        onGround1 = true
    }
    if (check2Player && (scene.tileHitFrom(guy2, CollisionDirection.Bottom) == 15 || scene.tileHitFrom(guy2, CollisionDirection.Top) == 15)) {
        onGround2 = true
    }
    if (scene.tileHitFrom(guy1, CollisionDirection.Right) == 3) {
        if (check2Player) {
            guy2.destroy(effects.disintegrate, 200)
        }
        p1Win = true
    }
    if (scene.tileHitFrom(guy2, CollisionDirection.Right) == 3) {
        guy1.destroy(effects.disintegrate, 200)
        p2Win = true
    }
})
game.onUpdate(function () {
    if (startscreenFinished && (!(gameBegun) && !(_2startscreenFinished))) {
        createMap()
        button1.destroy()
        button2.destroy()
        createGuy1()
        scene.centerCameraAt(guy1.x, 72)
        if (check2Player) {
            createGuy2()
            game.splash("Blue -> LeftArrow | Red -> RightArrow")
            info.startCountdown(5)
        } else {
            game.splash("Blue -> LeftArrow")
            info.startCountdown(5)
        }
        _2startscreenFinished = true
    }
})
game.onUpdate(function () {
    if (p1Win) {
        game.splash("Player 1 Wins!")
        game.over(true)
    }
    if (p2Win) {
        if (check2Player) {
            game.splash("Player 2 Wins!")
            game.over(true)
        }
        game.over(false)
    }
})
